import React from "react";

import styled from "styled-components";

import Arrow from "./Icons/Arrow";
import bgImg from "https://www.google.com/maps/uv?hl=fr&pb=!1s0xd9f8bc31af11adb%3A0x9e14215adca351f0!2m22!2m2!1i80!2i80!3m1!2i20!16m16!1b1!2m2!1m1!1e1!2m2!1m1!1e3!2m2!1m1!1e5!2m2!1m1!1e4!2m2!1m1!1e6!3m1!7e115!4shttps%3A%2F%2Flh5.googleusercontent.com%2Fp%2FAF1QipNjg0xQTveBEI6LunMLcqo5nfe9BZa4BEYZuWwn%3Dw213-h160-k-no!5slaboamfes%20-%20Recherche%20Google!15sCAESAggH&imagekey=!1e10!2sAF1QipNydK-B1rEC-RGwnO7nh9Z_L_UEBFA3lTbBWf2J&sa=X&ved=2ahUKEwj5vM36kLfkAhUKlhQKHYh9Cn0QoiowDHoECAkQBg";

const Section = styled.section`
  position: relative;
  padding-top: 138px;
  padding-bottom: 288px;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  background-attachment: fixed;
  background-image: url(${bgImg});

  &:after,
  &:before {
    content: "";
    width: 0;
    height: 0;
    position: absolute;
    bottom: -1px;
    border-bottom: 10px solid #fff;
    z-index: 2;
  }

  &:before {
    left: 0;
    border-right: 12px solid transparent;
    border-left: calc(50vw - 12px) solid #fff;
  }

  &:after {
    right: 0;
    border-left: 12px solid transparent;
    border-right: calc(50vw - 12px) solid #fff;
  }

  @media (min-width: 992px) {
    padding-left: 110px;
    padding-right: 110px;
    padding-top: 279px;
    padding-bottom: 111px;
  }

  .container {
    z-index: 1;
    user-select: none;
    cursor: default;
  }
`;

const BgOverlay = styled.div`
  background: linear-gradient(33deg, #8400ff, #413bff);
  opacity: 0.9;
  position: absolute;
  height: 100%;
  width: 100%;
  right: 0;
  bottom: 0;
  left: 0;
  top: 0;

  @media (min-width: 992px) {
    background-image: linear-gradient(62deg, #8400ff, #413bff);
  }
`;

const HomeTitle = styled.h1`
  font-weight: 300;
  text-align: center;
  color: #fff;
  font-size: 22px;
  line-height: 1.55;
  margin-bottom: 23px;

  @media (min-width: 992px) {
    font-size: 38px;
    line-height: 1.39;
    max-width: 1100px;
    margin-left: auto;
    margin-right: auto;
    margin-bottom: 0;
  }
`;

const SubTitle = styled.h5`
  font-size: 12px;
  font-weight: normal;
  line-height: 1.83;
  text-align: center;
  color: #ffffff;

  @media (min-width: 992px) {
    max-width: 385px;
    margin-left: auto;
    margin-right: auto;
    margin-bottom: 49px;
  }
`;

const Box = styled.div`
  width: 105px;
  height: 105px;
  border: 15px solid #fb434b;
  margin-left: auto;
  margin-right: auto;
  position: relative;
`;

const Home = () => {
  return (
    <Section id="home">
      <BgOverlay />
      <div className="container">
        <HomeTitle data-aos="zoom-in">
          It is a long established fact that a reader will be distracted by the
          readable content of a page when looking at its layout.
        </HomeTitle>
        <SubTitle
          data-aos="fade-up"
          data-aos-easing="ease"
          data-aos-delay="400"
        >
          Lorem Ipsum is simply dummy text of the printing and typesetting
          industry.
        </SubTitle>
        {/* <Box data-aos="fade-up" data-aos-easing="ease" data-aos-delay="800">
          <Arrow
            data-aos="fade-up"
            data-aos-easing="ease"
            data-aos-delay="1200"
          />
        </Box> */}
      </div>
    </Section>
  );
};

export default Home;
